import React, { useState } from 'react';

const foodItems = {
    'F001': { name: 'Pizza', rate: 10 },
    'F002': { name: 'Burger', rate: 5 },
    'F003': { name: 'Pasta', rate: 8 },
};

const FoodEntry = ({ onFoodSelect }) => {
    const [foodCode, setFoodCode] = useState('');
    const [quantity, setQuantity] = useState(1);
    const [error, setError] = useState('');

    const handleFoodCodeChange = (e) => {
        setFoodCode(e.target.value);
        setError('');
    };

    const handleQuantityChange = (e) => {
        setQuantity(e.target.value);
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        if (foodItems[foodCode]) {
            onFoodSelect(foodCode, quantity);
            setFoodCode('');
            setQuantity(1);
        } else {
            setError('Invalid food code');
        }
    };

    return (
        <div>
            <h2>Food Entry</h2>
            <form onSubmit={handleSubmit}>
                <div>
                    <label>
                        Food Code:
                        <input type="text" value={foodCode} onChange={handleFoodCodeChange} />
                    </label>
                </div>
                <div>
                    <label>
                        Quantity:
                        <input type="number" value={quantity} onChange={handleQuantityChange} min="1" />
                    </label>
                </div>
                <button type="submit">Add Food</button>
                {error && <p style={{ color: 'red' }}>{error}</p>}
            </form>
        </div>
    );
};

export default FoodEntry;
