import React, { useState, useEffect } from 'react';

const BillPrint = () => {
    const [foodItems, setFoodItems] = useState([]);
    const [billNumber] = useState(() => 'B' + Date.now().toString().slice(-6));
    const [currentDate] = useState(() => new Date().toLocaleDateString());
    const [currentTime] = useState(() => new Date().toLocaleTimeString());

    useEffect(() => {
        // Load food items from localStorage
        const savedFoods = localStorage.getItem('selectedFoods');
        if (savedFoods) {
            setFoodItems(JSON.parse(savedFoods));
        }
    }, []);

    const calculateSubtotal = () => {
        return foodItems.reduce((total, item) => total + (item.rate * item.quantity), 0);
    };

    const calculateTax = (subtotal) => {
        return subtotal * 0.18; // 18% GST
    };

    const calculateTotal = () => {
        const subtotal = calculateSubtotal();
        const tax = calculateTax(subtotal);
        return subtotal + tax;
    };

    const handlePrint = () => {
        window.print();
    };

    const handleClearBill = () => {
        if (window.confirm('Are you sure you want to clear the bill?')) {
            localStorage.removeItem('selectedFoods');
            setFoodItems([]);
        }
    };

    const subtotal = calculateSubtotal();
    const tax = calculateTax(subtotal);
    const total = calculateTotal();

    return (
        <div className="bill-print">
            <div className="bill-header">
                <h1>Maharashtra Hotel</h1>
                <div className="hotel-details">
                    <p>123 Main Street, Mumbai, Maharashtra</p>
                    <p>Phone: +91 9876543210 | Email: info@maharashtrahotel.com</p>
                    <p>GSTIN: 27ABCDE1234F1Z5</p>
                </div>
            </div>

            <div className="bill-info">
                <div>
                    <strong>Bill No:</strong>
                    {billNumber}
                </div>
                <div>
                    <strong>Date:</strong>
                    {currentDate}
                </div>
                <div>
                    <strong>Time:</strong>
                    {currentTime}
                </div>
            </div>

            {foodItems.length > 0 ? (
                <>
                    <table className="bill-table">
                        <thead>
                            <tr>
                                <th>Code</th>
                                <th>Item</th>
                                <th>Rate</th>
                                <th>Qty</th>
                                <th>Amount</th>
                            </tr>
                        </thead>
                        <tbody>
                            {foodItems.map((item, index) => (
                                <tr key={index}>
                                    <td>{item.code}</td>
                                    <td>{item.name}</td>
                                    <td>₹{item.rate}</td>
                                    <td>{item.quantity}</td>
                                    <td>₹{(item.rate * item.quantity).toFixed(2)}</td>
                                </tr>
                            ))}
                        </tbody>
                    </table>

                    <div className="bill-summary">
                        <div className="summary-row">
                            <span>Subtotal:</span>
                            <span>₹{subtotal.toFixed(2)}</span>
                        </div>
                        <div className="summary-row">
                            <span>GST (18%):</span>
                            <span>₹{tax.toFixed(2)}</span>
                        </div>
                        <div className="total-row">
                            <span>Total Amount:</span>
                            <span>₹{total.toFixed(2)}</span>
                        </div>
                    </div>

                    <div style={{ textAlign: 'center', marginTop: '30px', fontSize: '14px', color: '#666' }}>
                        <p>Thank you for visiting Maharashtra Hotel!</p>
                        <p>Visit again soon!</p>
                    </div>

                    <button onClick={handlePrint} className="print-button">
                        Print Bill
                    </button>
                    <button onClick={handleClearBill} className="clear-button">
                        Clear Bill
                    </button>
                </>
            ) : (
                <div className="no-items">
                    <p>No items to print. Please add items from the menu.</p>
                </div>
            )}
        </div>
    );
};

export default BillPrint;
