import React from 'react';

const BillCalculation = ({ foodItems }) => {
    const calculateTotalBill = () => {
        return foodItems.reduce((total, item) => total + (item.price * item.quantity), 0);
    };

    return (
        <div>
            <h2>Total Bill Calculation</h2>
            <p>Total Amount: ${calculateTotalBill().toFixed(2)}</p>
        </div>
    );
};

export default BillCalculation;
