import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import HomePage from './pages/HomePage';
import LoginPage from './pages/LoginPage';
import MenuPage from './pages/MenuPage';
import BillPage from './pages/BillPage';
import Header from './components/common/Header';
import Footer from './components/common/Footer';
import { isAuthenticated as checkAuth } from './services/authService';

const App = () => {
  const [isAuthenticated, setIsAuthenticated] = useState(false);

  useEffect(() => {
    // Check if user is already logged in when app loads
    setIsAuthenticated(checkAuth());
  }, []);

  const handleLogin = () => {
    setIsAuthenticated(true);
  };

  const handleLogout = () => {
    setIsAuthenticated(false);
    localStorage.removeItem('user');
  };

  return (
    <Router>
      <Header isAuthenticated={isAuthenticated} onLogout={handleLogout} />
      <Routes>
        <Route path="/" element={isAuthenticated ? <HomePage /> : <LoginPage onLogin={handleLogin} />} />
        <Route path="/login" element={<LoginPage onLogin={handleLogin} />} />
        <Route path="/home" element={isAuthenticated ? <HomePage /> : <LoginPage onLogin={handleLogin} />} />
        <Route path="/menu" element={isAuthenticated ? <MenuPage /> : <LoginPage onLogin={handleLogin} />} />
        <Route path="/bill" element={isAuthenticated ? <BillPage /> : <LoginPage onLogin={handleLogin} />} />
      </Routes>
      <Footer />
    </Router>
  );
};

export default App;
