import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { login } from '../../services/authService';
import './Login.css';

const Login = ({ onLogin }) => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');
    const navigate = useNavigate();

    const handleLogin = async (e) => {
        e.preventDefault();
        setError('');

        try {
            const loginSuccess = login(username, password);
            if (loginSuccess) {
                if (onLogin) {
                    onLogin(); // Update authentication state in parent
                }
                navigate('/home'); // Redirect to home page after successful login
            } else {
                setError('Invalid username or password');
            }
        } catch (err) {
            setError('Invalid username or password');
        }
    };

    return (
        <div className="login-container">
            <form className="login-form" onSubmit={handleLogin}>
                <h2>Hotel Login</h2>

                <div style={{ 
                    background: '#e8f4fd', 
                    padding: '15px', 
                    borderRadius: '6px', 
                    marginBottom: '20px',
                    fontSize: '14px',
                    color: '#0c5460'
                }}>
                    <strong>Available Hotel Logins:</strong>
                    <div style={{ marginTop: '10px' }}>
                        <div>üè® <strong>Hotel Matoshree:</strong></div>
                        <div style={{ marginLeft: '20px' }}>Username: matoshree_admin</div>
                        <div style={{ marginLeft: '20px' }}>Password: matoshree@2025</div>
                    </div>
                    <div style={{ marginTop: '10px' }}>
                        <div>üè® <strong>Maharashtra Hotel:</strong></div>
                        <div style={{ marginLeft: '20px' }}>Username: maharashtra_admin</div>
                        <div style={{ marginLeft: '20px' }}>Password: maharashtra@2025</div>
                    </div>
                </div>

                {error && <p className="error-message">{error}</p>}
                <div className="form-group">
                    <label>Username:</label>
                    <input
                        type="text"
                        value={username}
                        onChange={(e) => setUsername(e.target.value)}
                        placeholder="Enter your hotel admin username"
                        required
                    />
                </div>
                <div className="form-group">
                    <label>Password:</label>
                    <input
                        type="password"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                        placeholder="Enter your password"
                        required
                    />
                </div>
                <button type="submit" className="login-button">Login</button>
            </form>
        </div>
    );
};

export default Login;
